function getMathjaxCookie() {
    var mathjaxCookieName = "arxiv_mathjax";
    var dc = document.cookie;
    var prefix = mathjaxCookieName + "=";
    var begin = dc.indexOf(prefix);
    var end = document.cookie.indexOf(";", begin);
    var returnVal;
    if (end == -1) {
        end = dc.length
    }
    returnVal = unescape(dc.substring(begin + prefix.length, end));
    return returnVal
}
function setMathjaxCookie() {
    var mathjaxCookieName = "arxiv_mathjax";
    var value = "disabled";
    var cv = getMathjaxCookie();
    if (cv == "disabled") {
        value = "enabled"
    } else if (cv == "enabled") {
        value = "disabled"
    }
    var mathjaxCookieValue = value;
    var today = new Date;
    var expire = new Date;
    var nDays = 365;
    expire.setTime(today.getTime() + 36e5 * 24 * nDays);
    document.cookie = mathjaxCookieName + "=" + escape(value) + ";domain=.arxiv.org;path=/;expires=" + expire.toGMTString();
    window.location.reload()
}
function getInternetExplorerVersion() {
    var rv = -1;
    if (navigator.appName == "Microsoft Internet Explorer") {
        var ua = navigator.userAgent;
        var re = new RegExp("MSIE ([0-9]{1,}[.0-9]{0,})");
        if (re.exec(ua) != null)
            rv = parseFloat(RegExp.$1)
    }
    return rv
}
function mathjaxToggle(showErrors) {
    showErrors = showErrors || 0;
    var returnVal;
    returnVal = getMathjaxCookie();
    mjLink = document.getElementById("mathjax_toggle");
    if (returnVal === "enabled" || returnVal != "disabled") {
        if (mjLink) {
            mjLink.innerHTML = "Disable MathJax"
        }
        var errorConfig;
        if (showErrors != 0) {
            errorConfig = "        'color': 'red',\n" + "        'padding': '1px 1px',\n" + "        'border': '1px solid'\n"
        } else {
            errorConfig = "        'font-size': 'normal',\n" + "        'border': ''\n"
        }
        var head = document.getElementsByTagName("head")[0],
            script;
        script = document.createElement("script");
        script.type = "text/x-mathjax-config";
        script[window.opera ? "innerHTML" : "text"] = "MathJax.Hub.Config({\n" + "  messageStyle: 'none',\n" + "  extensions: ['tex2jax.js'],\n" + "  jax: ['input/TeX','output/HTML-CSS'],\n" + "  tex2jax: {\n" + "    inlineMath: [['$','$']],\n" + "    processEscapes: true,\n" + "    ignoreClass: '.*',\n" + "    processClass: 'mathjax.*',\n" + "  },\n" + "  TeX: {\n" + "    extensions: ['AMSmath.js', 'AMSsymbols.js', 'noErrors.js'],\n" + "    noErrors: {\n" + "      inlineDelimiters: ['$','$'],\n" + "      multiLine: false,\n" + "      style: {\n" + errorConfig + "      }\n" + "    }\n" + "  }\n" + "});";
        head.appendChild(script);
        script = document.createElement("script");
        script.type = "text/javascript";
        script.src = "https://static.arxiv.org/MathJax-2.7.3/MathJax.js";
        head.appendChild(script)
    } else {
        mjLink.innerHTML = "Enable MathJax"
    }
}

